% data
n = 8;

include "all_equal.mzn";
include "global_cardinality.mzn";

% load parameters
include "parameters.dzn";

int: daysPerWeek = 7;

% the number of weeks
int: numberOfWeeks;

int: numberOfDays = numberOfWeeks * daysPerWeek;

% the minimum number of days in a row with the same shift
int: s_min;

% the maximum number of days in a row with the same shift
int: s_max; 

% 3 shifts: early = 1, late = 2 and night shift = 3 + day off (rest day) = 0
int: numberOfShifts = 3;

% the shift requirement matrix, including at M_{i,j} the needed employees for day i and shift j
array[1..daysPerWeek, 1..numberOfShifts+1] of int: shiftRequirements;


% the variables X 
array[1..numberOfWeeks * daysPerWeek] of var 0..numberOfShifts: plan1d;
array[1..numberOfWeeks,1..daysPerWeek] of var 0..numberOfShifts: plan2d;
array[1..daysPerWeek,1..numberOfWeeks] of var 0..numberOfShifts: plan2dT;

% helper arrays
array[1..s_min,1..s_min] of var 0..numberOfShifts: s_min_arrays;
array[1..s_max,1..s_max] of var 0..numberOfShifts: s_max_arrays;

% convert the 2D plan into 1D
constraint forall(week in 1..numberOfWeeks, day in 1..daysPerWeek) (
                 plan2d[week, day] == plan1d[(week-1) * daysPerWeek + day]
           );  

% transpose the 2D plan
constraint forall(week in 1..numberOfWeeks, day in 1..daysPerWeek) (
                 plan2d[week, day] == plan2dT[day, week]
           );

% C_equalDays: constrains that weekend days (Saturday and Sunday) always have the same shift
constraint forall(week in 1..numberOfWeeks) (
               plan2d[week, daysPerWeek - 1] == plan2d[week, daysPerWeek]
           );                        


% create the sub arrays other the array bounds
constraint forall(i in 1..s_min, j in 1..s_min) (
                 s_min_arrays[i, j] == plan1d[((numberOfDays - s_min - 1 + i + j) mod numberOfDays) + 1]
           );                                            

% C_shiftRepetitions:for every shift type a minimum number of consecutive assignments to this shift is given
constraint forall(day in 1..numberOfDays - s_min) (
               plan1d[day] != plan1d[day+1] -> all_equal(plan1d[day+1..day+s_min])
           );     
% the constraints over the array bounds           
constraint forall(d in 1..s_min) (
               plan1d[d + numberOfDays - s_min] != plan1d[((d + numberOfDays - s_min) mod numberOfDays) +1] -> all_equal(s_min_arrays[d,1..s_min])
           );                     


% create the sub arrays other the array bounds
constraint forall(i in 1..s_max, j in 1..s_max) (
                 s_max_arrays[i, j] == plan1d[((numberOfDays - s_max - 2 + i + j) mod numberOfDays) + 1]
           );                      

% C_shiftRepetitions:for every shift type a maximum number of consecutive assignments to this shift is given           
constraint forall(day in 1..numberOfWeeks * daysPerWeek - s_max) (
               (all_equal(plan1d[day..day+s_max])) -> (plan1d[day] != plan1d[day + s_max])
           );               
% the constraints over the array bounds             
constraint forall(d in 1..s_max) (
               (all_equal(s_max_arrays[d, 1..s_max])) -> (plan1d[d + numberOfDays - s_max] != plan1d[d])
           );                     



% C_restDays: at least 2 days must be rest days every 2 weeks.
constraint forall(day in 1..(numberOfWeeks - 2) * daysPerWeek) (
               count(j in plan1d[day..day + daysPerWeek * 2])(j=0) >= 2                   
           );            
constraint forall(i in 1..2 * daysPerWeek-1) (
               count(j in plan1d[numberOfWeeks * daysPerWeek-i .. numberOfWeeks * daysPerWeek] ++ plan1d[1..2*daysPerWeek-i])(j=0) >= 2    
           );         

% C_shiftOrder: restricts the order of shifts. There is a forward rotating principle. This means, that after an early shift there can only follow a shift with the same or a higher value, or a rest shift.
constraint forall(day in 2..numberOfWeeks * daysPerWeek - 1) (
               plan1d[day] <= plan1d[day + 1] \/ plan1d[day+1] == 0
           );         
constraint plan1d[1] >= plan1d[numberOfDays] \/ plan1d[1] == 0;

% C_shiftRequirements: for every weekday for each shift the number of required staff is provided (e.g. usually less staff is required on the weekend) 
constraint forall(day in 1..daysPerWeek) (
              (global_cardinality(row(plan2dT,day),[0,1,2,3],row(shiftRequirements,day)))   
           );    

% solve the problem           
solve :: int_search(plan1d, input_order, indomain_min, complete)
      satisfy;           

% print the problem      
output [ show(row(plan2d,j)) ++ "\n" | j in 1..numberOfWeeks ] ++ ["\n"];

%% schurs numbers
%%
%% determine if n balls labelled 1..n
%% can be placed in c boxes with no box containing a triple {x,y,z} where x+y=z


int: n; %% number of balls
int: c; %% number of boxes

array[1..n] of var 1..c: box;

constraint forall(i in 1..n-1, j in i+1 .. n - i)(
	   	    box[i] != box[j] \/ 
		    box[i] != box[i+j] \/ 
		    box[j] != box[i+j]);

solve satisfy;

output ["n = ", show(n), ";\nc = ", show(c), ";\nbox = ",show(box),";\n"];

% The ternary Steiner problem of order n is to find n(n-1)/6 sets of elements in {1,2,...,n}
% such that each set contains three elements and any two sets have at most one element in common.
% For example, the following shows a solution for size n=7:
%
%      {1,2,3}, {1,4,5}, {1,6,7}, {2,4,6}, {2,5,7}, {3,4,7}, {3,5,6}

include "globals.mzn";

int: N = 7; 
int: NB = N *(N-1) div 6;
array[1..NB] of var set of 1..N: Sets;

% solve satisfy;
solve::set_search(Sets, first_fail, indomain_min, complete) satisfy;



constraint
   forall(i in index_set(Sets)) (
      card(Sets[i]) = 3
   )
   /\ 
   forall(i,j in index_set(Sets) where i < j) (
      card( Sets[i] intersect Sets[j]) <= 1
   )
   /\ % symmetry breaking
   decreasing(Sets)

;

output [
       "N: ", show(N), " NB: ", show(NB), "\n",
       "Sets: ", show(Sets)
];

% Stochastic General Assignment Problem 
% First stage:
%    assign task to machines 
% Second stage:
%    based on observed processign times, schedule taks on respective machines
% Objective:
%    minimise expected makespan

include "globals.mzn";

% Parameters

int: no_mach;   % Number of machines
int: no_jobs;   % Number of jobs
int: no_task;   % Number of total tasks
int: no_optt;   % Number of total optional tasks

set of int: Mach  = 1..no_mach;
set of int: Jobs  = 1..no_jobs;
set of int: Tasks = 1..no_task;
set of int: OptTs = 1..no_optt;

array [Jobs] of set of int: tasks;
array [Tasks] of set of int: optts;

array [OptTs] of int: optt_mach;
array [SCENARIOS1,OptTs] of int: optt_dur;


array [Jobs] of int: last_task = [ max(tasks[j]) | j in Jobs ];
%---------implications for multi scenarion solving ---------------
int: nbScenarios;
set of int: SCENARIOS1 = 1..nbScenarios;
int: first_scen;
int: last_scen;
set of int: SCENARIOS = first_scen..last_scen;
array[SCENARIOS1] of int: weights;

%-------end of multi scenario addons ----------------
array [Tasks] of int: task_job = 
    [ min(j in Jobs where t in tasks[j])(j) | t in Tasks ];
array [SCENARIOS,Tasks] of int: task_mins =
    array2d(SCENARIOS,Tasks,[ sum(k in tasks[task_job[t]])(if k < t then task_mind[s,k] else 0 endif)
    |   s in SCENARIOS, t in Tasks ]);
array [SCENARIOS,Tasks] of int: task_maxs =
    array2d(SCENARIOS,Tasks,[ t_max[s] -
        sum(k in tasks[task_job[t]])(if k < t then 0 else task_mind[s,k] endif)
    |   s in SCENARIOS,  t in Tasks ]);

array [SCENARIOS,Tasks] of int: task_mind =
    array2d(SCENARIOS,Tasks,[ min(o in optts[t])(optt_dur[s,o]) | s in SCENARIOS,t in Tasks ]);

array [SCENARIOS,Tasks] of int: task_maxd = 
    array2d(SCENARIOS,Tasks,[ max(o in optts[t])(optt_dur[s,o]) | s in SCENARIOS, t in Tasks ]);

    % Additional deirved parameters for optional tasks
    %
array [OptTs] of int: optt_task =
    [ min(t in Tasks where o in optts[t])(t) | o in OptTs ];

array[SCENARIOS1] of int: min_dur = [ min([optt_dur[s,t] | t in OptTs]) | s in SCENARIOS1];
array[SCENARIOS1] of int: max_dur = [ max([optt_dur[s,t] | t in OptTs]) | s in SCENARIOS1];
set of int: Durs = min(min_dur)..max(max_dur);

    % Parameters related to the planning horizon
    %
array[SCENARIOS1] of int: t_max = [sum(t in Tasks)(max(o in optts[t])(optt_dur[s,o])) | s in SCENARIOS1];

set of int: Times = 0..max(t_max);

%-----------------------------------------------------------------------------%
% Variables

    % Start time variables for tasks
    %
array [SCENARIOS,Tasks] of var Times: start =
    array2d(SCENARIOS,Tasks,[ let { var task_mins[s,t]..task_maxs[s,t]: k } in k | s in SCENARIOS, t in Tasks ]);

    % Duration variables for tasks
    %
array [SCENARIOS,Tasks] of var Durs: dur =
    array2d(SCENARIOS,Tasks,[ if task_mind[s,t] = task_maxd[s,t] then task_mind[s,t] else
        let { var task_mind[s,t]..task_maxd[s,t]: d } in d endif
    |   s in SCENARIOS,t in Tasks ]);

    % Variables whether an optional task is executed
    %
array [OptTs] of var bool: b;

array[SCENARIOS] of var Times: de_objective;

set of int: StochTimes = 0..sum(t_max);
var StochTimes: objective;
%-----------------------------------------------------------------------------%
% Constraints

    % Precedence relations
    %
constraint
    forall(s in SCENARIOS)(
        forall(j in Jobs, i in tasks[j] where i < last_task[j])(
            start[s,i] + dur[s,i] <= start[s,i + 1]
        )    
    );

    % Duration constraints
    %
constraint
    forall(o in OptTs,s in SCENARIOS)(
        let { int: t = optt_task[o] } in (
            if card(optts[t]) = 1 then
                b[o] = true
            else
                b[o] -> dur[s,t] = optt_dur[s,o]
            endif
        )
    );

    % Optional tasks' constraints
    %
constraint
    forall(t in Tasks where card(optts[t]) > 1)(
        ( sum(o in optts[t])(bool2int(b[o])) <= 1     )
    /\  ( exists(o in optts[t])(b[o])                 )
    );

constraint
    forall(t in Tasks where card(optts[t]) = 2)(
        let {
            int: o1 = min(optts[t]),
            int: o2 = max(optts[t])
        } in ( b[o1] <-> not(b[o2]) )
    );

    % Resource constraints
    %
constraint
    forall(m in Mach,s in SCENARIOS)(
        let {
            set of int: MTasks = { o | o in OptTs where optt_mach[o] = m }
        } in (
            cumulative(
                [ start[s,optt_task[o]] | o in MTasks ],
                [ optt_dur[s,o]         | o in MTasks ],
                [ bool2int(b[o])      | o in MTasks ],
                1
            )
        )
    );

% Objective constraint
constraint
    forall(s in SCENARIOS)(
        forall(j in Jobs)(start[s,last_task[j]] + dur[s,last_task[j]] <= de_objective[s])
    );
constraint 
        objective = sum(s in SCENARIOS)(weights[s]*de_objective[s]);
%-----------------------------------------------------------------------------%
% Solve item

solve 
    :: search
    minimize objective;

%------------------------------------------------------------------------------%
% Searches

ann: s_mindur   = int_search([dur[s,t] |s in SCENARIOS, t in Tasks], smallest, indomain_min, complete);
ann: s_minstart = int_search([start[s,t] |s in SCENARIOS, t in Tasks], smallest, indomain_min, complete);
ann: s_bool     = bool_search(b, input_order, indomain_max, complete);
ann: s_obj      = int_search(de_objective, input_order, indomain_min, complete);

ann: search = seq_search([s_mindur, s_bool, s_minstart, s_obj]);

%-----------------------------------------------------------------------------%
% Output

output 
[   "objective = ", show(de_objective), ";\n",
    "stoch obj = ", show(objective), ";\n",
    "start = ", show(start), ";\n",
    "dur = ", show(dur), ";\n",
    "b = ", show(b), ";\n",
];

