constraint forall (i,j in PuzzleRange) (
        sum(k in PuzzleRange)(puzzle[i,j,k]) == 1
);

solve satisfy;

output [ show_int(digs,puzzle[i,j]) ++ " " ++
        if j == N /\ i != N then "\n" else "" endif
        | i,j in PuzzleRange ] ++ ["\n"];
