solve minimize totalcost;

output ["cost = \(totalcost)\n"] ++
       [ "Prisoner \(p) " ++ if p in female then "(F)" else "(M)" endif ++ 
         " in [\(r[p]),\(c[p])].\n"
       | p in PRISONER ];



