% The balanced academic curriculum problem:
%
% A curriculum is a set of courses with prerequisites.
% Each course must be assigned within a set number of periods.
% A course cannot be scheduled before its prerequisites.
% Each course confers a number of academic credits (it's "load").
% Students have lower and upper bounds on the number of credits they can study for in a given period.
% Students have lower and upper bounds on the number of courses they can study for in a given period.
% The goal is to assign a period to every course satisfying these criteria, minimising the load for all periods.

include "globals.mzn";

int: n_courses;
int: n_periods;
int: load_per_period_lb;
int: load_per_period_ub;
int: courses_per_period_lb;
int: courses_per_period_ub;
array [1..n_courses] of int: course_load;
int: max_course_load = sum(c in courses)(course_load[c]);

set of int: courses = 1..n_courses;
set of int: periods = 1..n_periods;

% period course is assigned to
array [courses] of var periods: course_period;
% whether period i has course j assigned
array [periods, courses] of var 0..1: x;
% total load for each period
array [periods] of var load_per_period_lb..load_per_period_ub: load;
% optimisation target
var load_per_period_lb..load_per_period_ub: objective;

constraint forall(p in periods) (
    forall(c in courses) (x[p,c] = bool2int(course_period[c] = p)) /\
    sum(i in courses) (x[p,i]) >= courses_per_period_lb /\
    sum(i in courses) (x[p,i]) <= courses_per_period_ub /\
    load[p] = sum(c in courses) (x[p,c] * course_load[c]) /\
    load[p] >= load_per_period_lb /\
    load[p] <= objective
);

% prerequisite(a, b) means "course a has prerequisite course b".

predicate prerequisite(courses: a, courses: b) =
    course_period[b] < course_period[a];

% add some redundant linear constraints

constraint forall(p in 0..n_periods-1) (
    let {
		var 0..max_course_load: l = sum(c in courses) (bool2int(course_period[c] > p) * course_load[c])
	} in 
        l >= (n_periods-p) * load_per_period_lb /\
        l <= (n_periods-p) * objective
    );

solve :: seq_search([
      int_search([x[i,j] | i in periods, j in courses], input_order, indomain_max, complete),
      int_search([objective], input_order, indomain_min, complete)
    ]) minimize objective;

output 
    [show(c) ++ "-" ++ show(course_period[c]) ++ "\t" | c in courses ] ++ ["\n"] ++
    ["objective = ", show(objective)];

% scheduling ships entering and leaving a port through k channels
set of int: TYPE = 1..3;
int: entering = 1;
int: leaving  = 2;
int: dummy = 3;

int: nC; % number of channels
set of int: CHANNEL = 1..nC;
array[CHANNEL] of int: len;    % length in 100m units

int: nS; % number of ships
set of int: SHIP = 1..nS; % ships ENTER then LEAVE
set of int: SHIPE = 1..nS+nC; % ships ENTER then LEAVE then nC dummy ships

array[SHIP] of int: speed;   % time in minutes to traverse 100m
array[SHIP] of int: desired; % desired time to start to enter/leave 
array[SHIP] of entering..leaving: dirn;   % entering or leaving

int: leeway;  % leeway between 2 ships in 100m units
int: maxt;    % maximum time
set of int: TIME = 0..maxt;

array[SHIPE] of var TYPE: kind = dirn ++ [ dummy | i in 1..nC];
array[SHIPE] of var TIME:    start; % when ships enter the channel 
array[SHIPE] of var TIME:     end; % when ships leave the channel 
array[SHIPE] of var CHANNEL: channel; % which channel a ship is assigned to

array[SHIP] of var SHIPE:   next;  % the next ship in the same channel (or a dummy)

% dummy ships
constraint forall(s in nS + 1 .. nS + nC)(start[s] = maxt /\ end[s] = maxt);
constraint forall(s in nS + 1 .. nS + nC)(channel[s] = s - nS);

% relationships between start and ends
constraint forall(s in SHIP)(end[s] = start[s] + len[channel[s]]*speed[s]);


% the next ship are all different
include "alldifferent.mzn";
constraint alldifferent(next);

% no clashes in channel
constraint forall(s in SHIP)(if kind[s] + kind[next[s]] = entering+leaving then
                                end[s] <= start[next[s]]
                             else
                                start[s] + speed[s]*leeway <= start[next[s]] /\
                                end[s] + speed[s]*leeway <= end[next[s]]
                             endif);

% next ship is in same channel
constraint forall(s in SHIP)(channel[next[s]] = channel[s]);

solve minimize sum(s in SHIP)(abs(start[s] - desired[s]));

output [ "start = ",show(start),
