array[1..u] of var 0..n: x;
constraint forall(i in 1..u-1)(x[i] >= (x[i] != 0) + x[i+1]);

constraint forall(i in 1..k)(sum(j in 1..u)(x[j] in s[i]) <= 1);

solve maximize sum(x);

output ["x = ", show(x), ";\n"];

int: W; % number of Warehousse
set of int: WARE = 1..W;
int: F; % number of Factories
set of int: FACT = 1..F;

array[WARE] of int: demand;
array[FACT] of int: production;

array[FACT,WARE] of int: cost;

array[FACT,WARE] of var int: ship;

%int: m = max(demand);
%array[FACT,WARE] of var 0..m: ship;
constraint forall(f in FACT, w in WARE)(ship[f,w] >= 0);

constraint forall(w in WARE)
                 (sum(f in FACT)(ship[f,w]) >= demand[w]);

constraint forall(f in FACT)
                 (sum(w in WARE)(ship[f,w]) <= production[f]);

solve minimize sum(f in FACT, w in WARE)
                  (cost[f,w]*ship[f,w]);


output [ show_int(2,ship[f,w]) ++
         if w == W then "\n" else " " endif
       | f in FACT, w in WARE ];

% Number of different products
int: nproducts; 
set of int: Products = 1..nproducts;  

%profit per unit for each product
array[Products] of int: profit;
array[Products] of string: pname; 

%Number of resources
int: nresources; 
set of int: Resources = 1..nresources; 

%amount of each resource available
array[Resources] of int: capacity; 
array[Resources] of string: rname;

%units of each resource required to produce 1 unit of product
array[Products, Resources] of int: consumption; 
constraint assert(forall (r in Resources, p in Products) 
           (consumption[p,r] >= 0), "Error: negative consumption");

% bound on number of Products
int: mproducts = max (p in Products) 
                     (min (r in Resources where consumption[p,r] > 0) 
                          (capacity[r] div consumption[p,r]));

% Variables: how much should we make of each product
array[Products] of var 0..mproducts: produce;
array[Resources] of var 0..max(capacity): used;

% Production cannot use more than the available Resources:
constraint forall (r in Resources) (     
      used[r] = sum (p in Products)(consumption[p, r] * produce[p]) 
      /\ used[r] <= capacity[r]
);    

% Maximize profit
solve maximize sum (p in Products) (profit[p]*produce[p]);

output [ show(pname[p]) ++ " = " ++ show(produce[p]) ++ ";\n" |
         p in Products ] ++
       [ show(rname[r]) ++ " = " ++ show(used[r]) ++ ";\n" |
         r in Resources ];

int: n; % number of squares
set of int: SQUARE = 1..n;
int: maxl = sum(i in SQUARE)(i);
int: mina = sum(i in SQUARE)(i*i);

var n..maxl: height;
var n..maxl: width;
var mina .. n*maxl: area = height * width;

array[SQUARE] of var 0..maxl: x;
array[SQUARE] of var 0..maxl: y;

% squares fit in the rectangle
constraint forall(s in SQUARE)(x[s] + s <= width);
constraint forall(s in SQUARE)(y[s] + s <= height);

% non overlap
constraint forall(s1, s2 in SQUARE where s1 < s2)
                 (x[s1] + s1 <= x[s2] \/
                  x[s2] + s2 <= x[s1] \/  
                  y[s1] + s1 <= y[s2] \/ 
                  y[s2] + s2 <= y[s1]);
array[SQUARE] of int: size = [ i | i in SQUARE ];

% non overlap with global diffn
include "diffn.mzn";
%constraint diffn(x,y,size,size);

% redundant cumulative constraints
include "cumulative.mzn";
%constraint cumulative(x,size,size,height);
%constraint cumulative(y,size,size,width);

% variables ordered in reverse size x[n], y[n], x[n-1], y[n-1], ..., x[1], y[1]
%array[1..2*n] of var 0..maxl: vs = [ if i mod 2 = 0 then x[n+1 - i div 2] 
%                                     else y[n+1 - i div 2] endif | i in 2..2*n+1 ];

solve :: seq_search([
         int_search([area,height,width], input_order, indomain_min, complete) 
         %,int_search(vs, input_order, indomain_min, complete)
         ])
      minimize area;

output ["area = ",show(area), "\n"] ++
["height = ",show(height), "\n"] ++
["width = ",show(width), "\n"] ++
["x = ", show(x), "\n"] ++
["y = ", show(y), "\n"];

